$.fn.isOnScreen=function(){var t=$(window),o={top:t.scrollTop(),left:t.scrollLeft()};o.right=o.left+t.width(),o.bottom=o.top+t.height();var i=this.offset();return i.right=i.left+this.outerWidth(),i.bottom=i.top+this.outerHeight(),!(o.right<i.left||o.left>i.right||o.bottom<i.top||o.top>i.bottom)},$(document).ready(function(){$(window).scroll(function(){$("section").each(function(){console.log($(this).isOnScreen()?"In View: "+$(this).attr("class"):"Not In View: "+$(this).attr("class"))})})});